import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
    const [posts, setPosts] = useState([]);
    
    // componentDidMount
    useEffect(() => {
      fetch('https://jsonplaceholder.typicode.com/posts') //fetch data url
      .then(response => response.json())
      .then(posts => {
        setPosts(posts);
      });
    }, []);  
    return (
      <>
        <h1>Posts</h1>
        {
          posts.map(post => (
            <div key={post.id}>
              <h2><Link to={`/post/${post.id}`}>{post.title}</Link></h2>
              <p>{post.body}</p>
            </div>
          ))
        }
      </>   
    )
  }

  export default Home;
