import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const PostSingle = () => {
    const [post, setPost] = useState({});
    const { id } = useParams();
    // componentDidMount
    useEffect(() => {
        fetch(`https://jsonplaceholder.typicode.com/posts/${id}`) //fetch data url
        .then(response => response.json())
        .then(post => {
          setPost(post);
        });
      }, []);     
    return (
        <>
            <div key={post.id}>
              <h2>{post.title}</h2>
              <p>{post.body}</p>
            </div>
        </>
    )
}

export default PostSingle;
