import React, { useEffect, useState } from 'react';

import Header from './Header';
import Footer from './Footer';



const App = () => {
  const [posts, setPosts] = useState([]);

  // componentDidMount
  useEffect(() => {
    fetch() //fetch data url
    .then(response => response.json())
    .then(posts => {
      setPosts(posts);
    });
  }, []);

  return (
    <>
    <Header />
      [App]
    <Footer />
    </>
  );
}

export default App;
