import React, { useEffect, useState } from 'react';

import Header from './Header';
import Footer from './Footer';

const App = () => {
  const [posts, setPosts] = useState([]);

  // componentDidMount
  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/posts') //fetch data url
    .then(response => response.json())
    .then(posts => {
      setPosts(posts);
    });
  }, []);

  return (
    <>
      <Header />
      <h1>Posts</h1>
      {
        posts.map(post => (
          <div key={post.id}>
            <h2>{post.title}</h2>
            <p>{post.body}</p>
          </div>
        ))
      }
      <Footer />
    </>
  );
}

export default App;
